import React, { useEffect } from "react";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  useEffect(() => {
    console.log("Fetching chart data for:", ticker); // Log ticker
    const fetchChartData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        if (response.ok) {
          const data = await response.json();
          console.log("Chart data fetched:", data);
        } else {
          console.error("Failed to fetch chart data for:", ticker);
        }
      } catch (error) {
        console.error("Error fetching chart data:", error);
      }
    };

    fetchChartData();
  }, [ticker]);

  if (ticker === "TickerMissing") {
    return <p>No data available for this ticker.</p>;
  }

  return (
    <div>
      <h3>{ticker}</h3>
      <p>Entry: {entryPoint}, Exit: {exitPoint}</p>
      <p>{additionalData}</p>
    </div>
  );
};

export default CandlestickChart;
