import React, { useState } from "react";
import Navbar from "./components/Navbar";
import StockScanner from "./components/StockScanner";
import TickerNewsWidget from "./components/TickerNewsWidget";
import "./App.css";

const App = () => {
  const [stocks, setStocks] = useState([]); // State to manage stock data

  return (
    <>
      <Navbar />
      {/* Search Bar */}
      <div className="search-bar">
        <StockScanner onTickersChange={setStocks} />
      </div>

      {/* Stock Results Section */}
      <div className="stock-results">
        <h2 style={{ textAlign: "center" }}>Stock Results</h2>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Stock List */}
        <div className="stock-list">
          {stocks.map((stock) => (
            <div className="stock-card" key={stock.T}>
              <h4>{stock.T}</h4>
              <div className="chart-container">
                {/* The chart remains functional with dynamic data */}
                <CandlestickChart
                  ticker={stock.T}
                  entryPoint={stock.c * 0.95}
                  exitPoint={stock.c * 1.1}
                  additionalData={{
                    volatility: `${(stock.volatility * 100).toFixed(2)}%`,
                    change: `${(stock.price_change * 100).toFixed(2)}%`,
                    rsi: stock.rsi || "N/A",
                  }}
                />
              </div>
            </div>
          ))}
        </div>

        {/* News Widget */}
        <div className="news-widget">
          <TickerNewsWidget tickers={stocks.map((stock) => stock.T)} />
        </div>
      </div>
    </>
  );
};

export default App;
