import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";

const CandlestickChart = ({ ticker }) => {
  const chartContainerRef = useRef(null);
  const chartRef = useRef(null);
  const candlestickSeriesRef = useRef(null);

  useEffect(() => {
    // Function to handle resizing of the chart
    const handleResize = () => {
      if (chartRef.current && chartContainerRef.current) {
        const newWidth = Math.min(chartContainerRef.current.clientWidth, 1200); // Limit max width to 1200px
        const newHeight = 400; // Fixed height

        // Update chart dimensions
        chartRef.current.applyOptions({ width: newWidth, height: newHeight });

        // Manually adjust canvas dimensions if necessary
        const canvasElements = chartContainerRef.current.querySelectorAll("canvas");
        canvasElements.forEach((canvas) => {
          canvas.style.width = `${newWidth}px`;
          canvas.style.height = `${newHeight}px`;
        });
      }
    };

    // Initialize the chart
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 1200,
      height: 400,
      layout: { backgroundColor: "#ffffff", textColor: "#000000" },
      grid: { vertLines: { color: "#eeeeee" }, horzLines: { color: "#eeeeee" } },
      priceScale: { borderColor: "#cccccc" },
      timeScale: { borderColor: "#cccccc" },
    });

    candlestickSeriesRef.current = chart.addCandlestickSeries();
    chartRef.current = chart;

    // Fetch initial data
    const fetchInitialData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();

        if (data.dates) {
          const formattedData = data.dates.map((date, index) => ({
            time: date,
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          candlestickSeriesRef.current.setData(formattedData);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchInitialData();

    // Add ResizeObserver for dynamic resizing
    const resizeObserver = new ResizeObserver(() => {
      handleResize(); // Adjust chart dimensions when the container resizes
    });

    resizeObserver.observe(chartContainerRef.current);

    // Cleanup on component unmount
    return () => {
      resizeObserver.disconnect();
      if (chartRef.current) chartRef.current.remove();
    };
  }, [ticker]);

  return (
    <div className="chart-container" ref={chartContainerRef}>
      {/* Chart container */}
    </div>
  );
};

export default CandlestickChart;
