import React, { useEffect, useState } from "react";
import {
  ChartCanvas,
  Chart,
  XAxis,
  YAxis,
  CandlestickSeries,
  LineSeries,
  CurrentCoordinate,
  EdgeIndicator,
} from "react-financial-charts";
import { scaleTime } from "d3-scale";
import { timeFormat } from "d3-time-format";
import "./CandlestickChart.css"; // Ensure the updated CSS file is linked

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();
        if (response.ok) {
          const formattedData = data.dates.map((date, index) => ({
            date: new Date(date),
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          setChartData(formattedData);
        } else {
          setError(data.error || "Failed to fetch chart data.");
        }
      } catch (err) {
        setError("Failed to fetch chart data.");
      }
    };

    fetchChartData();
  }, [ticker]);

  if (error) {
    return <p style={{ color: "red" }}>{error}</p>;
  }

  if (chartData.length === 0) {
    return <p>Loading chart...</p>;
  }

  return (
    <div className="chart-container">
      <h3 className="chart-title">{ticker}</h3>
      <div style={{ width: "100%", overflowX: "auto" }}>
        <ChartCanvas
          data={chartData}
          width={800}
          height={400}
          ratio={1}
          margin={{ left: 50, right: 50, top: 10, bottom: 30 }}
          xAccessor={(d) => d.date}
          xScale={scaleTime()}
          displayXAccessor={(d) => timeFormat("%Y-%m-%d")(d.date)}
        >
          <Chart id={1} yExtents={(d) => [d.high, d.low]}>
            <XAxis />
            <YAxis />
            <CandlestickSeries />
            <LineSeries yAccessor={() => entryPoint} stroke="green" strokeDasharray="5 5" />
            <LineSeries yAccessor={() => exitPoint} stroke="red" strokeDasharray="5 5" />
            <CurrentCoordinate yAccessor={() => entryPoint} fill="green" />
            <CurrentCoordinate yAccessor={() => exitPoint} fill="red" />
            <EdgeIndicator itemType="last" orient="right" edgeAt="right" yAccessor={(d) => d.close} />
          </Chart>
        </ChartCanvas>
      </div>
      <div className="chart-info">
        <p>{additionalData}</p>
        <p>
          <strong>Entry Point:</strong> ${entryPoint.toFixed(2)} <br />
          <strong>Exit Point:</strong> ${exitPoint.toFixed(2)}
        </p>
      </div>
    </div>
  );
};

export default CandlestickChart;
