import React, { useEffect, useState } from "react";
import { ChartCanvas, Chart, XAxis, YAxis, CandlestickSeries, LineSeries } from "react-financial-charts";
import { timeFormat } from "d3-time-format";
import { utcDay } from "d3-time";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();
        if (response.ok) {
          const formattedData = data.dates.map((date, index) => ({
            date: new Date(date),
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          setChartData(formattedData);
        } else {
          setError(data.error || "Failed to fetch chart data.");
        }
      } catch (err) {
        setError("Failed to fetch chart data.");
      }
    };

    fetchChartData();
  }, [ticker]);

  if (error) {
    return <p style={{ color: "red" }}>{error}</p>;
  }

  if (chartData.length === 0) {
    return <p>Loading chart...</p>;
  }

  return (
    <div>
      <h3 style={{ textAlign: "center" }}>{ticker}</h3>
      <ChartCanvas
        data={chartData}
        width={800}
        height={400}
        ratio={1}
        margin={{ left: 50, right: 50, top: 10, bottom: 30 }}
        xAccessor={(d) => d.date}
        xScale={utcDay}
        displayXAccessor={(d) => timeFormat("%Y-%m-%d")(d.date)}
      >
        <Chart id={1} yExtents={(d) => [d.high, d.low]}>
          <XAxis />
          <YAxis />
          <CandlestickSeries />
          <LineSeries yAccessor={() => entryPoint} stroke="green" />
          <LineSeries yAccessor={() => exitPoint} stroke="red" />
        </Chart>
      </ChartCanvas>
      <p style={{ textAlign: "center" }}>{additionalData}</p>
    </div>
  );
};

export default CandlestickChart;
