import React, { useState } from "react";
import "../components/StocksPage.css";
import {
  StockChartComponent,
  StockChartSeriesCollectionDirective,
  StockChartSeriesDirective,
  Inject,
  DateTime,
  Tooltip,
  RangeTooltip,
  Crosshair,
  LineSeries,
  CandleSeries,
  HiloOpenCloseSeries,
  RangeNavigator,
  EmaIndicator,
  RsiIndicator,
  BollingerBands,
  TmaIndicator,
  MomentumIndicator,
  SmaIndicator,
  AtrIndicator,
  AccumulationDistributionIndicator,
  MacdIndicator,
  StochasticIndicator,
} from "@syncfusion/ej2-react-charts";

const StocksPage = () => {
  const [ticker, setTicker] = useState("");
  const [selectedTicker, setSelectedTicker] = useState("AAPL"); // Default ticker
  const [chartData, setChartData] = useState([]); // Holds dynamic chart data

  const handleSearch = async () => {
    if (ticker.trim() !== "") {
      setSelectedTicker(ticker.toUpperCase());

      // Fetch data dynamically for the selected ticker
      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker.toUpperCase()}`
        );
        const data = await response.json();

        if (data.dates) {
          const formattedData = data.dates.map((date, index) => ({
            x: new Date(date),
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          setChartData(formattedData);
        } else {
          setChartData([]);
          alert("No data available for the entered stock ticker.");
        }
      } catch (error) {
        console.error("Error fetching stock data:", error);
        setChartData([]);
        alert("Failed to load stock data.");
      }
    }
  };

  return (
    <div className="stocks-page">
      {/* Header Section */}
      <div className="stock-header">
        <div className="stock-info">
          <h2>{selectedTicker}</h2>
          <p className="price">
            $1.57 <span className="change positive">+0.0497 (+3.27%)</span>
          </p>
          <p className="date">As of Jan 21, 2025 1:28:55 PM ET</p>
        </div>
        <div className="stock-actions">
          <button className="buy-btn">Buy</button>
          <button className="sell-btn">Sell</button>
        </div>
      </div>

      {/* Search Field */}
      <div className="search-stock">
        <input
          type="text"
          placeholder="Enter ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Stock Chart Section */}
        <div className="chart-section">
          <div id="chart-container" style={{ width: "100%", height: "100%" }}>
            <StockChartComponent
              id="stockchart"
              primaryXAxis={{
                valueType: "DateTime",
                majorGridLines: { width: 0 },
                labelFormat: "MMM dd",
                intervalType: "Days",
                crosshairTooltip: { enable: true },
              }}
              primaryYAxis={{
                labelFormat: "${value}",
                majorGridLines: { width: 0 },
                rangePadding: "None",
                majorTickLines: { width: 0 },
              }}
              toolbarSettings={{
                visible: true, // Enables toolbar
                items: ["Zoom", "Pan", "Reset", "Indicators", "Periods"],
              }}
              indicators={["Ema", "Rsi", "BollingerBands"]} // Configures indicators
              periods={[
                { intervalType: "Days", interval: 1, text: "1D" },
                { intervalType: "Months", interval: 1, text: "1M" },
                { intervalType: "Months", interval: 6, text: "6M" },
                { intervalType: "Years", interval: 1, text: "1Y" },
                { intervalType: "Years", interval: 5, text: "5Y" },
              ]}
              height="90%"
              width="100%"
            >
              <Inject
                services={[
                  DateTime,
                  Tooltip,
                  RangeTooltip,
                  Crosshair,
                  LineSeries,
                  CandleSeries,
                  HiloOpenCloseSeries,
                  RangeNavigator,
                  EmaIndicator,
                  RsiIndicator,
                  BollingerBands,
                  TmaIndicator,
                  MomentumIndicator,
                  SmaIndicator,
                  AtrIndicator,
                  AccumulationDistributionIndicator,
                  MacdIndicator,
                  StochasticIndicator,
                ]}
              />
              <StockChartSeriesCollectionDirective>
                <StockChartSeriesDirective
                  dataSource={chartData}
                  type="Candle"
                  animation={{ enable: true }}
                ></StockChartSeriesDirective>
              </StockChartSeriesCollectionDirective>
            </StockChartComponent>
          </div>
        </div>

        {/* Supporting Information Panels */}
        <div className="info-panels">
          <div className="info-panel">
            <p><strong>Bid x Size (XNMS):</strong> $1.56 x 1</p>
            <p><strong>Ask x Size (XNMS):</strong> $1.57 x 3</p>
            <p><strong>Volume:</strong> 135,089</p>
            <p><strong>Day Range:</strong> $1.42 - $1.59</p>
            <p><strong>52-Week Range:</strong> $0.7100 - $7.17</p>
          </div>
          <div className="info-panel">
            <p><strong>Volatility:</strong> N/A</p>
            <p><strong>Change:</strong> N/A</p>
            <p><strong>RSI:</strong> N/A</p>
            <p><strong>Entry Point:</strong> $1.54</p>
            <p><strong>Exit Point:</strong> $1.78</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StocksPage;
