import React, { useState } from "react";
import Navbar from "./components/Navbar";
import StockScanner from "./components/StockScanner";
import TickerNewsWidget from "./components/TickerNewsWidget";
import "./App.css";

const App = () => {
  const [tickers, setTickers] = useState([]); // State to hold tickers

  return (
    <>
      {/* Navbar Component */}
      <Navbar />

      <div className="app-layout">
        {/* Search Bar */}
        <div className="search-bar">
          <StockScanner onTickersChange={setTickers} />
        </div>

        {/* Stock Results Header */}
        <div className="stock-results">
          <h2>Stock Results</h2>
        </div>

        {/* Main Content */}
        <div className="main-content">
          {/* Stock List Section */}
          <div className="stock-list">
            {tickers.length > 0 ? (
              tickers.map((ticker, index) => (
                <div className="stock-card" key={index}>
                  <h3>{ticker}</h3>
                  <p>Sample data for {ticker}</p>
                </div>
              ))
            ) : (
              <p>No stock data available. Please refine your search.</p>
            )}
          </div>

          {/* News Widget */}
          <div className="news-widget">
            <TickerNewsWidget tickers={tickers} />
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
