import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";

const CandlestickChart = ({ ticker, entryPoint, exitPoint }) => {
  const chartContainerRef = useRef(null);
  const chartRef = useRef(null);

  useEffect(() => {
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 1200,
      height: 400,
      layout: { backgroundColor: "#ffffff", textColor: "#000000" },
      grid: { vertLines: { color: "#eeeeee" }, horzLines: { color: "#eeeeee" } },
      priceScale: {
        position: "right",
        borderColor: "#cccccc",
      },
      timeScale: {
        borderColor: "#cccccc",
      },
    });

    const candlestickSeries = chart.addCandlestickSeries();

    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();

        if (data.dates) {
          const formattedData = data.dates.map((date, index) => ({
            time: date,
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          candlestickSeries.setData(formattedData);
        }
      } catch (error) {
        console.error("Error fetching chart data:", error);
      }
    };

    fetchChartData();

    return () => {
      chart.remove();
    };
  }, [ticker]);

  return (
    <div
      style={{
        display: "flex",
        gap: "20px",
        alignItems: "flex-start",
        justifyContent: "center",
        maxWidth: "1200px",
        margin: "0 auto",
      }}
    >
      {/* Chart Container */}
      <div
        ref={chartContainerRef}
        style={{
          flex: 2,
          height: "400px",
          border: "2px solid #ccc",
          borderRadius: "5px",
          backgroundColor: "#ffffff",
        }}
      ></div>

      {/* Widget */}
      <div
        style={{
          flex: 1,
          border: "2px solid #ccc",
          borderRadius: "5px",
          padding: "10px",
          backgroundColor: "#ffffff",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h3 style={{ margin: "0 0 10px 0", textAlign: "center" }}>{ticker}</h3>
        <p><strong>Entry Point:</strong> ${entryPoint.toFixed(2)}</p>
        <p><strong>Exit Point:</strong> ${exitPoint.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default CandlestickChart;
