import React, { useEffect, useState } from "react";
import {
  ChartComponent,
  SeriesCollectionDirective,
  SeriesDirective,
  Inject,
  DateTime,
  CandleSeries,
  Tooltip,
  Zoom,
  Crosshair,
} from "@syncfusion/ej2-react-charts";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();

        if (data.dates) {
          const formattedData = data.dates.map((date, index) => ({
            x: new Date(date), // Syncfusion requires a date object
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          setChartData(formattedData);
        }
      } catch (error) {
        console.error("Error fetching chart data:", error);
      }
    };

    fetchChartData();
  }, [ticker]);

  return (
    <div className="parent-container">
      {/* Chart Container */}
      <ChartComponent
        id="candlestick-chart"
        primaryXAxis={{ valueType: "DateTime", labelFormat: "MMM dd", intervalType: "Days" }}
        primaryYAxis={{ labelFormat: "${value}" }}
        tooltip={{ enable: true }}
        crosshair={{ enable: true, lineType: "Both" }}
        zoomSettings={{ enableMouseWheelZooming: true, mode: "XY" }}
        height="400px"
      >
        <Inject services={[CandleSeries, DateTime, Tooltip, Zoom, Crosshair]} />
        <SeriesCollectionDirective>
          <SeriesDirective
            dataSource={chartData}
            xName="x"
            open="open"
            high="high"
            low="low"
            close="close"
            type="Candle"
          />
        </SeriesCollectionDirective>
      </ChartComponent>

      {/* Widget Container */}
      <div className="widget-container">
        <h3>{ticker}</h3>
        <p><strong>Entry Point:</strong> ${entryPoint.toFixed(2)}</p>
        <p><strong>Exit Point:</strong> ${exitPoint.toFixed(2)}</p>
        <div>
          <p><strong>Additional Data:</strong></p>
          <ul>
            <li>Volatility: {additionalData?.volatility || "N/A"}</li>
            <li>Change: {additionalData?.change || "N/A"}</li>
            <li>RSI: {additionalData?.rsi || "N/A"}</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default CandlestickChart;
