import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const chartContainerRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    if (!chartContainerRef.current) return;

    // Initialize the chart
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 1200,
      height: chartContainerRef.current.clientHeight || 400,
      layout: { backgroundColor: "#ffffff", textColor: "#000000" },
      grid: { vertLines: { color: "#eeeeee" }, horzLines: { color: "#eeeeee" } },
    });
    chartInstance.current = chart;

    const candlestickSeries = chart.addCandlestickSeries();

    // Fetch and set data to the chart
    const fetchChartData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        const data = await response.json();
        if (data.dates) {
          const formattedData = data.dates.map((date, index) => ({
            time: date,
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          candlestickSeries.setData(formattedData);
        }
      } catch (error) {
        console.error("Error fetching candlestick data:", error);
      }
    };

    fetchChartData();

    // Throttling the ResizeObserver
    let resizeTimeout;
    const handleResize = () => {
      clearTimeout(resizeTimeout);
      resizeTimeout = setTimeout(() => {
        if (chartContainerRef.current) {
          const width = chartContainerRef.current.clientWidth || 1200;
          const height = chartContainerRef.current.clientHeight || 400;
          console.log("Resizing chart to:", width, height);
          chart.applyOptions({ width, height });
        }
      }, 200); // 200ms debounce time
    };

    const resizeObserver = new ResizeObserver(handleResize);

    resizeObserver.observe(chartContainerRef.current);

    // Cleanup on unmount
    return () => {
      clearTimeout(resizeTimeout);
      resizeObserver.disconnect();
      chart.remove();
    };
  }, [ticker]);

  return (
    <div className="chart-widget-container">
      {/* Chart Section */}
      <div className="chart-wrapper" ref={chartContainerRef}></div>

      {/* Widget Section */}
      <div className="widget-container">
        <h3>{ticker}</h3>
        <p>
          <strong>Volatility:</strong> {additionalData?.volatility || "N/A"}
        </p>
        <p>
          <strong>Change:</strong> {additionalData?.change || "N/A"}
        </p>
        <p>
          <strong>RSI:</strong> {additionalData?.rsi || "N/A"}
        </p>
        <p>
          <strong>Entry Point:</strong> ${entryPoint.toFixed(2)}
        </p>
        <p>
          <strong>Exit Point:</strong> ${exitPoint.toFixed(2)}
        </p>
      </div>
    </div>
  );
};

export default CandlestickChart;
