import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";
import "./CandlestickChart.css";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const chartContainerRef = useRef(null);
  const chartRef = useRef(null); // Ref to track the chart instance
  const currentPriceLineRef = useRef(null); // Ref to track the current price line
  const intervalRef = useRef(null); // Ref to track the interval ID

  useEffect(() => {
    // Initialize the chart
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 700,
      height: 400,
      layout: {
        backgroundColor: "#ffffff",
        textColor: "#000000",
      },
      grid: {
        vertLines: { color: "#eeeeee" },
        horzLines: { color: "#eeeeee" },
      },
      priceScale: { borderColor: "#cccccc" },
      timeScale: { borderColor: "#cccccc" },
    });

    chartRef.current = chart; // Store the chart instance
    const candlestickSeries = chart.addCandlestickSeries();

    // Fetch initial data
    const fetchInitialData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        const data = await response.json();

        if (data.dates && data.open && data.high && data.low && data.close) {
          const formattedData = data.dates.map((date, index) => ({
            time: date,
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          candlestickSeries.setData(formattedData);

          // Get the current price
          const currentPrice = formattedData[formattedData.length - 1].close;
          const currentPriceColor = currentPrice > entryPoint ? "green" : "red";

          // Add the current price line and store its reference
          currentPriceLineRef.current = candlestickSeries.createPriceLine({
            price: currentPrice,
            color: currentPriceColor,
            lineWidth: 2,
            lineStyle: 0,
            axisLabelVisible: true,
            title: `Current: ${currentPrice.toFixed(2)}`,
          });

          // Add entry price line
          candlestickSeries.createPriceLine({
            price: entryPoint,
            color: "green",
            lineWidth: 1,
            lineStyle: 2,
            axisLabelVisible: true,
            title: `Entry: ${entryPoint.toFixed(2)}`,
          });

          // Add exit price line
          candlestickSeries.createPriceLine({
            price: exitPoint,
            color: "red",
            lineWidth: 1,
            lineStyle: 2,
            axisLabelVisible: true,
            title: `Exit: ${exitPoint.toFixed(2)}`,
          });
        }
      } catch (error) {
        console.error("Error fetching initial data:", error);
      }
    };

    fetchInitialData();

    // Real-time updates
    const updateChart = async () => {
      if (!chartRef.current) return; // Guard clause to prevent updates if the chart is disposed
      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        const data = await response.json();

        if (data.dates && data.open && data.high && data.low && data.close) {
          const latestData = {
            time: data.dates[data.dates.length - 1],
            open: data.open[data.open.length - 1],
            high: data.high[data.high.length - 1],
            low: data.low[data.low.length - 1],
            close: data.close[data.close.length - 1],
          };
          candlestickSeries.update(latestData);

          // Update the current price line
          const currentPrice = latestData.close;
          const currentPriceColor = currentPrice > entryPoint ? "green" : "red";

          if (currentPriceLineRef.current) {
            currentPriceLineRef.current.applyOptions({
              price: currentPrice,
              color: currentPriceColor,
              title: `Current: ${currentPrice.toFixed(2)}`,
            });
          }
        }
      } catch (error) {
        console.error("Error updating chart:", error);
      }
    };

    intervalRef.current = setInterval(updateChart, 5000); // Fetch new data every 5 seconds

    // Resize the chart on window resize
    const handleResize = () => {
      if (chartRef.current) {
        chart.applyOptions({ width: chartContainerRef.current.clientWidth });
      }
    };
    window.addEventListener("resize", handleResize);

    return () => {
      clearInterval(intervalRef.current); // Clear interval on unmount
      intervalRef.current = null;
      window.removeEventListener("resize", handleResize);
      if (chartRef.current) {
        chartRef.current.remove(); // Properly dispose of the chart
        chartRef.current = null;
      }
    };
  }, [ticker, entryPoint, exitPoint]);

  return (
    <div className="chart-container">
      <h3 className="chart-title">{ticker}</h3>
      <div ref={chartContainerRef} className="chart-wrapper"></div>
      <div className="chart-info">
        <p>{additionalData}</p>
        <p>
          <strong>Entry Point:</strong> ${entryPoint.toFixed(2)} <br />
          <strong>Exit Point:</strong> ${exitPoint.toFixed(2)} <br />
          <strong>Current Price:</strong> Displayed on chart
        </p>
      </div>
    </div>
  );
};

export default CandlestickChart;
