import React, { useState, useEffect } from "react";
import Plot from "react-plotly.js";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        if (!ticker) {
          throw new Error("Ticker is missing.");
        }
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        if (!response.ok) {
          throw new Error(`Failed to fetch chart data for ${ticker}`);
        }
        const data = await response.json();
        setChartData(data);
      } catch (err) {
        setError(err.message);
      }
    };
  
    fetchChartData();
  }, [ticker]);
  

  if (error) {
    return <p style={{ color: "red" }}>{error}</p>;
  }

  if (!chartData) {
    return <p>Loading chart...</p>;
  }

  const { dates, open, high, low, close, currentPrice } = chartData;

  return (
    <div className="stock-card">
      {/* Stock Information */}
      <div className="stock-info">
        <h4>{ticker}</h4>
        <p>
          <strong>Entry Point:</strong> $
          {entryPoint !== undefined && entryPoint !== null
            ? entryPoint.toFixed(2)
            : "N/A"}
        </p>
        <p>
          <strong>Exit Point:</strong> $
          {exitPoint !== undefined && exitPoint !== null
            ? exitPoint.toFixed(2)
            : "N/A"}
        </p>
        <p>
          <strong>Current Price:</strong> $
          {currentPrice !== undefined && currentPrice !== null
            ? currentPrice.toFixed(2)
            : "N/A"}
        </p>
        {additionalData && <p>{additionalData}</p>}
      </div>
  
      {/* Candlestick Chart */}
      <div className="chart-container">
        <Plot
          data={[
            {
              x: dates,
              open: open,
              high: high,
              low: low,
              close: close,
              type: "candlestick",
              name: `${ticker} Candlestick`,
            },
            {
              x: [dates[0], dates[dates.length - 1]],
              y: [entryPoint, entryPoint],
              mode: "lines",
              name: "Entry Point",
              line: { color: "green", width: 2, dash: "dashdot" },
            },
            {
              x: [dates[0], dates[dates.length - 1]],
              y: [exitPoint, exitPoint],
              mode: "lines",
              name: "Exit Point",
              line: { color: "red", width: 2, dash: "dashdot" },
            },
          ]}
          layout={{
            autosize: true,
            margin: { l: 40, r: 40, t: 30, b: 40 }, // Adjust margins for better spacing
            height: 400, // Increase the height of the chart
            width: 600, // Increase the width of the chart
            responsive: true,
          }}
          config={{ responsive: true }}
        />
      </div>
    </div>
  );
  
};

export default CandlestickChart;
