import React, { useState } from "react";
import Navbar from "./components/Navbar";
import StockScanner from "./components/StockScanner";
import TickerNewsWidget from "./components/TickerNewsWidget";
import "./App.css";

const App = () => {
  const [tickers, setTickers] = useState([]);

  return (
    <>
      <Navbar />
      {/* Independent Search Bar */}
      <div className="search-bar">
        <StockScanner onTickersChange={setTickers} />
      </div>

      {/* Main Content Layout */}
      <div className="app-layout">
        {/* Stock List */}
        <div className="stock-list">
          {tickers.map((ticker) => (
            <div key={ticker} className="stock-card">
              <h3>{ticker}</h3>
              <p>Sample data for {ticker}</p>
            </div>
          ))}
        </div>

        {/* News Widget */}
        <div className="news-widget">
          <TickerNewsWidget tickers={tickers} />
        </div>
      </div>
    </>
  );
};

export default App;
