import React, { useEffect, useState } from "react";
import axios from "axios";
import CandlestickChart from "./CandlestickChart";

const StockScanner = () => {
  const [stocks, setStocks] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/scan-stocks");
        setStocks(response.data.candidates);
      } catch (err) {
        setError("Failed to fetch stocks.");
      }
    };
    fetchStocks();
  }, []);

  return (
    <div>
      <h2>Top Stocks for Gains</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {stocks.map((stock) => (
          <li key={stock.ticker}>
            <strong>{stock.ticker}</strong> - ${stock.last_price.toFixed(2)}
            <CandlestickChart ticker={stock.ticker} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StockScanner;
