import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const chartRef = useRef();

  useEffect(() => {
    if (ticker) {
      // Fetch chart data dynamically
      fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`)
        .then((response) => response.json())
        .then((data) => {
          // Render chart using D3.js
          const svg = d3.select(chartRef.current);
          svg.selectAll("*").remove(); // Clear existing content

          // Render your chart logic here using the fetched data
          // Example:
          svg.append("text")
            .attr("x", 10)
            .attr("y", 20)
            .text(`Chart for ${ticker}`);
        })
        .catch((error) => console.error("Error fetching candlestick data:", error));
    }
  }, [ticker]);

  return (
    <div>
      <svg ref={chartRef} width={300} height={200}></svg>
      <div>
        <p>Volatility: {additionalData.volatility}</p>
        <p>Change: {additionalData.change}</p>
        <p>RSI: {additionalData.rsi}</p>
        <p>Entry Point: ${entryPoint.toFixed(2)}</p>
        <p>Exit Point: ${exitPoint.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default CandlestickChart;
