import React, { useEffect, useState } from "react";
import "./TickerNewsWidget.css";

const TickerNewsWidget = ({ tickers }) => {
  const [news, setNews] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNewsForTickers = async () => {
      setLoading(true);

      try {
        // Send a single request with all tickers as a comma-separated string
        const tickersParam = tickers.join(",");
        const response = await fetch(`http://localhost:5000/api/ticker-news?ticker=${tickersParam}`);
        const data = await response.json();

        // Update the state with news grouped by ticker
        setNews(data);
      } catch (error) {
        console.error("Error fetching news:", error);
        setNews({});
      } finally {
        setLoading(false);
      }
    };

    if (tickers.length > 0) {
      fetchNewsForTickers();
    } else {
      setNews({});
      setLoading(false);
    }
  }, [tickers]);

  if (loading) {
    return <div>Loading news...</div>;
  }

  if (Object.keys(news).length === 0) {
    return <div>No news available for the selected stocks.</div>;
  }

  return (
    <div className="ticker-news-widget">
      <h4>Latest Stock News</h4>
      {Object.entries(news).map(([ticker, articles]) => (
        <div key={ticker} className="ticker-news-section">
          <h5>{ticker}</h5>
          {Array.isArray(articles) && articles.length > 0 ? (
            articles.map((article, index) => (
              <div key={index} className="article">
                <a href={article.article_url} target="_blank" rel="noopener noreferrer">
                  {article.title}
                </a>
                <p>{article.description}</p>
              </div>
            ))
          ) : (
            <p>No news available for {ticker}.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default TickerNewsWidget;
