import React, { useState, useEffect } from "react";
import ErrorBoundary from "./ErrorBoundary"; // Import the ErrorBoundary component
import "./StocksPage.css";

import {
  StockChartComponent,
  StockChartSeriesCollectionDirective,
  StockChartSeriesDirective,
  StockChartIndicatorsDirective,
  StockChartIndicatorDirective,
  Inject,
  DateTime,
  Tooltip,
  RangeTooltip,
  Crosshair,
  LineSeries,
  CandleSeries,
  Legend,
  Export,
  EmaIndicator,
  TmaIndicator,
  SmaIndicator,
  MomentumIndicator,
  AtrIndicator,
  AccumulationDistributionIndicator,
  BollingerBands,
  MacdIndicator,
  StochasticIndicator,
  RsiIndicator,
} from "@syncfusion/ej2-react-charts";

const StocksPage = () => {
  const [ticker, setTicker] = useState(""); // Input for ticker search
  const [selectedTicker, setSelectedTicker] = useState("AAPL"); // Default ticker
  const [chartData, setChartData] = useState([]); // Holds chart data
  const [error, setError] = useState(""); // Tracks errors during data fetching
  const [selectedIndicator, setSelectedIndicator] = useState(""); // Selected indicator

  // Dropdown options for indicators
  const indicators = [
    { value: "", label: "None" },
    { value: "Ema", label: "EMA" },
    { value: "Sma", label: "SMA" },
    { value: "Tma", label: "TMA" },
    { value: "Macd", label: "MACD" },
    { value: "Rsi", label: "RSI" },
    { value: "BollingerBands", label: "Bollinger Bands" },
    { value: "Atr", label: "ATR" },
    { value: "Momentum", label: "Momentum" },
    { value: "Stochastic", label: "Stochastic" },
  ];

  // Fetch candlestick chart data
  const fetchChartData = async (tickerSymbol) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/candlestick?ticker=${tickerSymbol}`
      );
      const data = await response.json();
      if (data.dates) {
        const formattedData = data.dates.map((date, index) => ({
          x: new Date(date),
          open: data.open[index],
          high: data.high[index],
          low: data.low[index],
          close: data.close[index],
        }));
        setChartData(formattedData);
        setError("");
      } else {
        setChartData([]);
        setError("No data available for the selected ticker.");
      }
    } catch (err) {
      console.error("Error fetching chart data:", err);
      setChartData([]); // Reset chart data in case of an error
      setError("Failed to load chart data.");
    }
  };

  // Effect to fetch chart data when ticker changes
  useEffect(() => {
    fetchChartData(selectedTicker);
  }, [selectedTicker]);

  const handleSearch = () => {
    if (ticker.trim() !== "") {
      setSelectedTicker(ticker.toUpperCase());
    }
  };

  const periods = [
    { intervalType: "Months", interval: 1, text: "1M" },
    { intervalType: "Months", interval: 3, text: "3M" },
    { intervalType: "Months", interval: 6, text: "6M" },
    { intervalType: "Years", interval: 1, text: "YTD" },
    { intervalType: "Years", interval: 3, text: "All" },
  ];

  return (
    <div className="stocks-page" style={{ padding: "20px" }}>
      {/* Header Section */}
      <div className="stock-header" style={{ marginBottom: "20px" }}>
        <h2 style={{ textAlign: "center", fontSize: "24px" }}>
          {selectedTicker} Stock Analysis
        </h2>
      </div>

      {/* Indicator Dropdown */}
      <div
        className="indicator-dropdown"
        style={{ display: "flex", justifyContent: "center", marginBottom: "20px" }}
      >
        <label htmlFor="indicator-select" style={{ marginRight: "10px" }}>
          Select Indicator:
        </label>
        <select
          id="indicator-select"
          value={selectedIndicator}
          onChange={(e) => setSelectedIndicator(e.target.value)}
          style={{
            padding: "10px",
            fontSize: "16px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        >
          {indicators.map((indicator) => (
            <option key={indicator.value} value={indicator.value}>
              {indicator.label}
            </option>
          ))}
        </select>
      </div>

      {/* Chart Section */}
      <div
        id="chart-container"
        style={{
          width: "100%",
          height: "500px",
          backgroundColor: "#fff",
          borderRadius: "8px",
          padding: "10px",
        }}
      >
        {error ? (
          <p style={{ color: "red", textAlign: "center" }}>{error}</p>
        ) : (
          <ErrorBoundary>
            {chartData.length > 0 ? (
              <StockChartComponent
                id="stockchart"
                primaryXAxis={{
                  valueType: "DateTime",
                  labelFormat: "MMM dd",
                  majorGridLines: { width: 0 },
                }}
                primaryYAxis={{
                  labelFormat: "${value}",
                  majorGridLines: { width: 0 },
                  rangePadding: "None",
                }}
                legendSettings={{ visible: true }}
                title={`${selectedTicker} Stock Analysis`}
                toolbarSettings={{
                  visible: true, // Ensure the toolbar is displayed
                }}
                indicators={[]} // Add any indicators if needed
                periods={periods} // Chart time periods
                height="100%"
                width="100%"
              >
                <Inject
                  services={[
                    DateTime,
                    Tooltip,
                    RangeTooltip,
                    Crosshair,
                    LineSeries,
                    CandleSeries,
                    Legend,
                    Export,
                    EmaIndicator,
                    TmaIndicator,
                    SmaIndicator,
                    MomentumIndicator,
                    AtrIndicator,
                    AccumulationDistributionIndicator,
                    BollingerBands,
                    MacdIndicator,
                    StochasticIndicator,
                    RsiIndicator,
                  ]}
                />
                <StockChartSeriesCollectionDirective>
                  <StockChartSeriesDirective
                    dataSource={chartData}
                    xName="x"
                    open="open"
                    high="high"
                    low="low"
                    close="close"
                    type="Candle"
                    animation={{ enable: true }}
                  />
                </StockChartSeriesCollectionDirective>

                {/* Add Dynamic Indicators */}
                {selectedIndicator && (
                  <StockChartIndicatorsDirective>
                    <StockChartIndicatorDirective
                      type={selectedIndicator}
                      field="Close"
                      seriesName={selectedTicker}
                      fill="#606eff"
                      xName="x"
                      high="high"
                      low="low"
                      open="open"
                      close="close"
                      period={14} // Adjust period as needed
                    />
                  </StockChartIndicatorsDirective>
                )}
              </StockChartComponent>
            ) : (
              <p style={{ textAlign: "center", color: "gray" }}>
                Loading chart data, please wait...
              </p>
            )}
          </ErrorBoundary>
        )}
      </div>
    </div>
  );
};

export default StocksPage;
