import React, { useState, useEffect } from "react";

const TickerNewsWidget = ({ tickers }) => {
  const [newsData, setNewsData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNews = async () => {
      if (tickers.length === 0) {
        setNewsData({});
        setLoading(false);
        return;
      }

      try {
        setLoading(true);

        // Fetch news for all tickers
        const response = await fetch(
          `http://localhost:5000/api/ticker-news?tickers=${tickers.join(",")}`
        );

        if (!response.ok) {
          throw new Error("Failed to fetch news.");
        }

        const data = await response.json();

        // Ensure data is structured properly
        setNewsData(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching news:", error);
        setNewsData({});
        setLoading(false);
      }
    };

    fetchNews();
  }, [tickers]);

  if (loading) {
    return <p>Loading news...</p>;
  }

  return (
    <div className="news-widget">
      <h4>Latest Stock News</h4>
      {tickers.length > 0 ? (
        tickers.map((ticker) => (
          <div key={ticker} className="news-section">
            <h5>{ticker}</h5>
            {newsData[ticker]?.length > 0 ? (
              newsData[ticker].map((newsItem, index) => (
                <div key={index} className="news-item">
                  <a
                    href={newsItem.article_url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <h6>{newsItem.title}</h6>
                  </a>
                  <p>{newsItem.description}</p>
                  <p>
                    Sentiment:{" "}
                    <span
                      style={{
                        color:
                          newsItem.insights[0]?.sentiment === "positive"
                            ? "green"
                            : newsItem.insights[0]?.sentiment === "negative"
                            ? "red"
                            : "gray",
                      }}
                    >
                      {newsItem.insights[0]?.sentiment || "N/A"}
                    </span>
                  </p>
                  <p>
                    Reasoning: {newsItem.insights[0]?.sentiment_reasoning || "N/A"}
                  </p>
                </div>
              ))
            ) : (
              <p>No news available for {ticker}.</p>
            )}
          </div>
        ))
      ) : (
        <p>No news available for the selected stocks.</p>
      )}
    </div>
  );
};

export default TickerNewsWidget;
