import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";
import "./CandlestickChart.css";

const CandlestickChart = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const chartContainerRef = useRef(null);
  const chartRef = useRef(null); // Chart instance
  const candlestickSeriesRef = useRef(null); // Candlestick series
  const priceLineRefs = useRef({}); // Price lines (entry, exit, current)
  const isMounted = useRef(false); // Mounted state
  const intervalRef = useRef(null); // Interval ID

  useEffect(() => {
    isMounted.current = true; // Mark component as mounted

    // Initialize chart
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 700,
      height: 400,
      layout: { backgroundColor: "#ffffff", textColor: "#000000" },
      grid: { vertLines: { color: "#eeeeee" }, horzLines: { color: "#eeeeee" } },
      priceScale: { borderColor: "#cccccc" },
      timeScale: { borderColor: "#cccccc" },
    });

    chartRef.current = chart; // Store chart reference
    const candlestickSeries = chart.addCandlestickSeries();
    candlestickSeriesRef.current = candlestickSeries;

    const fetchInitialData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        const data = await response.json();

        if (data.dates && data.open && data.high && data.low && data.close) {
          const formattedData = data.dates.map((date, index) => ({
            time: date,
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          candlestickSeries.setData(formattedData);

          // Set price lines
          const currentPrice = formattedData[formattedData.length - 1].close;
          const currentPriceColor = currentPrice > entryPoint ? "green" : "red";

          priceLineRefs.current.currentPrice = candlestickSeries.createPriceLine({
            price: currentPrice,
            color: currentPriceColor,
            lineWidth: 2,
            title: `Current: ${currentPrice.toFixed(2)}`,
          });

          priceLineRefs.current.entry = candlestickSeries.createPriceLine({
            price: entryPoint,
            color: "green",
            lineWidth: 1,
            lineStyle: 2,
            title: `Entry: ${entryPoint.toFixed(2)}`,
          });

          priceLineRefs.current.exit = candlestickSeries.createPriceLine({
            price: exitPoint,
            color: "red",
            lineWidth: 1,
            lineStyle: 2,
            title: `Exit: ${exitPoint.toFixed(2)}`,
          });
        }
      } catch (error) {
        if (isMounted.current) console.error("Error fetching data:", error);
      }
    };

    fetchInitialData();

    // Real-time updates
    const updateChart = async () => {
      if (!isMounted.current || !chartRef.current) return; // Prevent updates if unmounted

      try {
        const response = await fetch(
          `http://localhost:5000/api/candlestick?ticker=${ticker}`
        );
        const data = await response.json();

        if (data.dates && data.open && data.high && data.low && data.close) {
          const latestData = {
            time: data.dates[data.dates.length - 1],
            open: data.open[data.open.length - 1],
            high: data.high[data.high.length - 1],
            low: data.low[data.low.length - 1],
            close: data.close[data.close.length - 1],
          };

          candlestickSeries.update(latestData);

          // Update current price line
          const currentPrice = latestData.close;
          const currentPriceColor = currentPrice > entryPoint ? "green" : "red";

          if (priceLineRefs.current.currentPrice) {
            priceLineRefs.current.currentPrice.applyOptions({
              price: currentPrice,
              color: currentPriceColor,
              title: `Current: ${currentPrice.toFixed(2)}`,
            });
          }
        }
      } catch (error) {
        if (isMounted.current) console.error("Error updating chart:", error);
      }
    };

    intervalRef.current = setInterval(updateChart, 5000); // Update every 5 seconds

    // Resize chart on window resize
    const handleResize = () => {
      if (chartRef.current) {
        chart.applyOptions({ width: chartContainerRef.current.clientWidth });
      }
    };
    window.addEventListener("resize", handleResize);

    return () => {
      isMounted.current = false; // Mark component as unmounted
      clearInterval(intervalRef.current); // Clear interval
      intervalRef.current = null;

      if (chartRef.current) {
        chart.remove(); // Properly dispose of chart
        chartRef.current = null;
      }
      window.removeEventListener("resize", handleResize);
    };
  }, [ticker, entryPoint, exitPoint]);

  return (
    <div className="chart-container">
      <h3 className="chart-title">{ticker}</h3>
      <div ref={chartContainerRef} className="chart-wrapper"></div>
      <div className="chart-info">
        <p>{additionalData}</p>
        <p>
          <strong>Entry Point:</strong> ${entryPoint.toFixed(2)} <br />
          <strong>Exit Point:</strong> ${exitPoint.toFixed(2)} <br />
          <strong>Current Price:</strong> Displayed on chart
        </p>
      </div>
    </div>
  );
};

export default CandlestickChart;
