import React, { useEffect, useState, useRef } from "react";
import {
  ChartComponent,
  SeriesCollectionDirective,
  SeriesDirective,
  Inject,
  DateTime,
  CandleSeries,
  Tooltip,
  Zoom,
  Crosshair,
} from "@syncfusion/ej2-react-charts";
import { registerLicense } from "@syncfusion/ej2-base";

// Register the Syncfusion license
registerLicense("YOUR_LICENSE_KEY");

const CandlestickChartStockPage = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState("");
  const [isReady, setIsReady] = useState(false); // Ensure chart DOM is ready
  const chartRef = useRef(null);

  useEffect(() => {
    // Fetch chart data for the specific ticker
    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();

        if (data.dates) {
          const formattedData = data.dates.map((date, index) => ({
            x: new Date(date),
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          setChartData(formattedData);
          setError("");
        } else {
          setError("No data available for this stock.");
        }
      } catch (err) {
        console.error(`Error fetching data for ${ticker}:`, err);
        setError("Failed to load chart data.");
      }
    };

    fetchChartData();
  }, [ticker]);

  useEffect(() => {
    if (chartRef.current) {
      setIsReady(true);
    }
  }, [chartRef]);

  return (
    <div className="chart-container-stock-page" style={{ height: "600px", width: "100%" }}>
      {error ? (
        <p style={{ color: "red", textAlign: "center" }}>{error}</p>
      ) : !isReady ? (
        <p style={{ textAlign: "center" }}>Loading chart...</p>
      ) : chartData.length === 0 ? (
        <p style={{ textAlign: "center" }}>No data available for the chart.</p>
      ) : (
        <ChartComponent
          ref={chartRef}
          id={`chart-${ticker}`}
          primaryXAxis={{
            valueType: "DateTime",
            labelFormat: "MMM dd",
            intervalType: "Days",
            crosshairTooltip: { enable: !!chartData.length },
          }}
          primaryYAxis={{
            labelFormat: "${value}",
            crosshairTooltip: { enable: !!chartData.length },
          }}
          tooltip={{ enable: !!chartData.length }}
          crosshair={{
            enable: !!chartData.length,
            lineType: "Both",
          }}
          zoomSettings={{
            enableMouseWheelZooming: true,
            enablePinchZooming: true,
            mode: "XY",
          }}
          height="100%"
          width="100%"
        >
          <Inject services={[CandleSeries, DateTime, Tooltip, Zoom, Crosshair]} />
          <SeriesCollectionDirective>
            <SeriesDirective
              dataSource={chartData}
              xName="x"
              open="open"
              high="high"
              low="low"
              close="close"
              type="Candle"
            />
          </SeriesCollectionDirective>
        </ChartComponent>
      )}
    </div>
  );
};

export default CandlestickChartStockPage;
