import React, { useEffect, useState } from "react";
import CandlestickChart from "./CandlestickChart";
import "./StockScanner.css";

const StockScanner = ({ onTickersChange }) => {
  const [stocks, setStocks] = useState([]);
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const [minRSI, setMinRSI] = useState(30);
  const [maxRSI, setMaxRSI] = useState(70);
  const [volumeSurge, setVolumeSurge] = useState(1.2);
  const [error, setError] = useState("");

  // Fetch stocks based on criteria
  const fetchStocks = async () => {
    try {
      const queryParams = new URLSearchParams({
        min_price: minPrice || "0",
        max_price: maxPrice || "1000000",
        min_rsi: minRSI,
        max_rsi: maxRSI,
        volume_surge: volumeSurge,
      });

      const response = await fetch(
        `http://localhost:5000/api/scan-stocks?${queryParams.toString()}`
      );
      const data = await response.json();

      if (response.ok) {
        if (data.candidates && data.candidates.length > 0) {
          setStocks(data.candidates);
          setError("");
          if (onTickersChange) {
            const tickers = data.candidates.map((stock) => stock.T);
            onTickersChange(tickers); // Pass tickers to parent
          }
        } else {
          setStocks([]);
          setError("No stocks found. Please adjust your criteria.");
          if (onTickersChange) onTickersChange([]);
        }
      } else {
        setError(data.error || "Failed to fetch stocks.");
      }
    } catch (err) {
      setError("Failed to fetch stocks.");
      console.error(err);
    }
  };

  return (
    <div className="stock-scanner">
      <h2 style={{ textAlign: "center" }}>Find Stocks by Custom Criteria</h2>
      <div className="filter-criteria">
        {/* Input fields and search button */}
        <button onClick={fetchStocks}>Search Stocks</button>
      </div>
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
      <div className="stock-list">
        {stocks.map((stock) => (
          <div className="stock-card" key={stock.T}>
            <CandlestickChart
              ticker={stock.T}
              entryPoint={stock.c * 0.95}
              exitPoint={stock.c * 1.1}
              additionalData={{
                volatility: `${(stock.volatility * 100).toFixed(2)}%`,
                change: `${(stock.price_change * 100).toFixed(2)}%`,
                rsi: stock.rsi || "N/A",
              }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default StockScanner;
