import React, { useState } from "react";
import Navbar from "./components/Navbar";
import StockScanner from "./components/StockScanner";
import CandlestickChart from "./components/CandlestickChart";
import "./App.css";

const App = () => {
  const [stocks, setStocks] = useState([]);

  return (
    <>
      <Navbar />
      <div className="app-layout">
        {/* Search Bar */}
        <div className="search-bar">
          <StockScanner onTickersChange={setStocks} />
        </div>

        {/* Stock Results Section */}
        <div className="stock-results">
          <h2>Stock Results</h2>
        </div>

        {/* Main Content */}
        <div className="main-content">
          {/* Stock List */}
          <div className="stock-list">
            {stocks.map((stock, index) => (
              <div className="stock-card" key={index}>
                <CandlestickChart
                  ticker={stock.T}
                  entryPoint={stock.c * 0.95}
                  exitPoint={stock.c * 1.1}
                  additionalData={{
                    volatility: `${(stock.volatility * 100).toFixed(2)}%`,
                    change: `${(stock.price_change * 100).toFixed(2)}%`,
                    rsi: stock.rsi || "N/A",
                  }}
                />
              </div>
            ))}
          </div>

          {/* News Widget */}
          <div className="news-widget">
            <h4>Latest Stock News</h4>
            {stocks.map((stock, index) => (
              <div key={index} className="news-section">
                <h5>{stock.T}</h5>
                <p>No news available for {stock.T}.</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
