import { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

function StockTracker({ selectedTickers }) {
  const [stockData, setStockData] = useState({});

  useEffect(() => {
    // Track selected stocks dynamically
    selectedTickers.forEach((ticker) => {
      socket.emit("track_stock", { ticker });
    });

    // Listen for stock updates
    socket.on("stock_update", (data) => {
      setStockData((prevData) => ({
        ...prevData,
        [data.ticker]: data,
      }));
    });

    // Cleanup: Stop tracking stocks when unselected
    return () => {
      selectedTickers.forEach((ticker) => {
        socket.emit("untrack_stock", { ticker });
      });
      socket.off("stock_update");
    };
  }, [selectedTickers]);

  return (
    <div>
      {selectedTickers.length === 0 ? (
        <p>üìå Select stocks to track.</p>
      ) : (
        selectedTickers.map((ticker) => (
          <div key={ticker}>
            <h2>
              {ticker} - üí≤
              {stockData[ticker] ? stockData[ticker].price.toFixed(2) : "Loading..."}
            </h2>
            <p>
              ‚è≥ Last Updated:{" "}
              {stockData[ticker] ? new Date(stockData[ticker].timestamp).toLocaleTimeString() : "-"}
            </p>
          </div>
        ))
      )}
    </div>
  );
}

export default StockTracker;
