import React, { useState, useEffect } from "react";
import ErrorBoundary from "./ErrorBoundary";
import "./StocksPage.css";
import StockTracker from "./StockTracker";
import {
  StockChartComponent,
  StockChartSeriesCollectionDirective,
  StockChartSeriesDirective,
  Inject,
  DateTime,
  Tooltip,
  RangeTooltip,
  Crosshair,
  LineSeries,
  CandleSeries,
  Legend,
  Export,
  EmaIndicator,
  TmaIndicator,
  SmaIndicator,
  MomentumIndicator,
  AtrIndicator,
  AccumulationDistributionIndicator,
  BollingerBands,
  MacdIndicator,
  StochasticIndicator,
  RsiIndicator,
} from "@syncfusion/ej2-react-charts";
import { DropDownListComponent } from "@syncfusion/ej2-react-dropdowns";

const StocksPage = () => {
  const [ticker, setTicker] = useState(""); // Input for ticker search
  const [selectedTicker, setSelectedTicker] = useState("AAPL"); // Default ticker
  const [chartData, setChartData] = useState([]); // Holds chart data
  const [error, setError] = useState(""); // Tracks errors during data fetching
  const [selectedIndicator, setSelectedIndicator] = useState("None"); // Selected Indicator
  const [indicators, setIndicators] = useState([]); // Current indicators array

  // Available indicators
  const availableIndicators = [
    { text: "None", value: [] },
    { text: "EMA", value: ["EmaIndicator"] },
    { text: "SMA", value: ["SmaIndicator"] },
    { text: "RSI", value: ["RsiIndicator"] },
    { text: "MACD", value: ["MacdIndicator"] },
    { text: "Bollinger Bands", value: ["BollingerBands"] },
  ];

  // Update indicators dynamically based on dropdown selection
  const handleIndicatorChange = (e) => {
    const selected = availableIndicators.find((item) => item.text === e.itemData.text);
    setSelectedIndicator(e.itemData.text);
    setIndicators(selected?.value || []);
  };

  // Fetch candlestick chart data
  const fetchChartData = async (tickerSymbol) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/candlestick?ticker=${tickerSymbol}`
      );
      const data = await response.json();
      if (data.dates) {
        const formattedData = data.dates.map((date, index) => ({
          x: new Date(date),
          open: data.open[index],
          high: data.high[index],
          low: data.low[index],
          close: data.close[index],
        }));
        setChartData(formattedData);
        setError("");
      } else {
        setChartData([]);
        setError("No data available for the selected ticker.");
      }
    } catch (err) {
      console.error("Error fetching chart data:", err);
      setChartData([]);
      setError("Failed to load chart data.");
    }
  };

  // Effect to fetch chart data when ticker changes
  useEffect(() => {
    fetchChartData(selectedTicker);
  }, [selectedTicker]);

  const handleSearch = () => {
    if (ticker.trim() !== "") {
      setSelectedTicker(ticker.toUpperCase());
    }
  };

  const periods = [
    { intervalType: "Months", interval: 1, text: "1M" },
    { intervalType: "Months", interval: 3, text: "3M" },
    { intervalType: "Months", interval: 6, text: "6M" },
    { intervalType: "Years", interval: 1, text: "YTD" },
    { intervalType: "Years", interval: 3, text: "All" },
  ];

  return (
    <div className="stocks-page" style={{ padding: "20px" }}>
      {/* Header Section */}
      <div className="stock-header" style={{ marginBottom: "20px" }}>
        <h2 style={{ textAlign: "center", fontSize: "24px" }}>
          {selectedTicker} Stock Analysis
        </h2>
      </div>

      {/* Real-Time Stock Tracker */}
      <div className="realtime-stock-tracker" style={{ marginBottom: "20px" }}>
        <h3 style={{ textAlign: "center" }}>Real-Time Stock Tracker</h3>
        <StockTracker ticker={selectedTicker} />
      </div>

      {/* Search Field */}
      <div
        className="search-stock"
        style={{
          marginBottom: "20px",
          display: "flex",
          alignItems: "center",
          gap: "10px",
        }}
      >
        <input
          type="text"
          placeholder="Enter stock ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value)}
          style={{
            padding: "10px",
            fontSize: "16px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <button
          onClick={handleSearch}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            borderRadius: "5px",
            backgroundColor: "#007bff",
            color: "#fff",
            border: "none",
            cursor: "pointer",
          }}
        >
          Search
        </button>
      </div>

      {/* Chart Section */}
      <div
        id="chart-container"
        style={{
          width: "100%",
          height: "500px",
          backgroundColor: "#fff",
          borderRadius: "8px",
          padding: "10px",
        }}
      >
        <div className="indicator-dropdown" style={{ marginBottom: "10px" }}>
          <DropDownListComponent
            id="indicator-dropdown"
            dataSource={availableIndicators}
            fields={{ text: "text", value: "text" }}
            placeholder="Select Indicator"
            change={handleIndicatorChange}
          />
        </div>
        {error ? (
          <p style={{ color: "red", textAlign: "center" }}>{error}</p>
        ) : (
          <ErrorBoundary>
            {chartData.length > 0 ? (
              <StockChartComponent
              id="stockchart"
              primaryXAxis={{
                  valueType: "DateTime",
                  labelFormat: "MMM dd",
                  majorGridLines: { width: 0 },
              }}
              primaryYAxis={{
                  labelFormat: "${value}",
                  majorGridLines: { width: 0 },
                  rangePadding: "None",
              }}
              legendSettings={{
                  visible: true, // Ensure the legend is visible
                  toggleVisibility: false, // Disable default toggle functionality
              }}
              title={`${selectedTicker} Stock Analysis`}
              height="100%"
              width="100%"
              legendRender={(args) => {
                  // Add custom class to legend text
                  args.text = `<span class="custom-legend-text">${args.text}</span>`;
              }}
              legendClick={(args) => {
                  console.log(`Legend clicked: ${args.text}`);
                  // Perform any desired actions here (e.g., toggle visibility manually)
              }}
          >
              <Inject
                  services={[
                      DateTime,
                      Tooltip,
                      RangeTooltip,
                      Crosshair,
                      LineSeries,
                      CandleSeries,
                      Legend,
                      Export,
                      EmaIndicator,
                      RsiIndicator,
                      BollingerBands,
                      TmaIndicator,
                      MomentumIndicator,
                      SmaIndicator,
                      AtrIndicator,
                      AccumulationDistributionIndicator,
                      MacdIndicator,
                      StochasticIndicator,
                  ]}
              />
              <StockChartSeriesCollectionDirective>
                  <StockChartSeriesDirective
                      dataSource={chartData}
                      xName="x"
                      open="open"
                      high="high"
                      low="low"
                      close="close"
                      type="Candle"
                  />
              </StockChartSeriesCollectionDirective>
          </StockChartComponent>
                    
            ) : (
              <p style={{ textAlign: "center", color: "gray" }}>
                Loading chart data, please wait...
              </p>
            )}
          </ErrorBoundary>
        )}
      </div>
    </div>
  );
};

export default StocksPage;
