import React, { useState, useEffect } from "react";
import ErrorBoundary from "./ErrorBoundary";
import StockTracker from "./StockTracker";
import io from "socket.io-client";
import "./StocksPage.css"; // Ensure this file has the Syncfusion imports

import {
  StockChartComponent,
  StockChartSeriesCollectionDirective,
  StockChartSeriesDirective,
  Inject,
  DateTime,
  Tooltip,
  RangeTooltip,
  Crosshair,
  LineSeries,
  CandleSeries,
  Legend,
  Export,
  EmaIndicator,
  TmaIndicator,
  SmaIndicator,
  MomentumIndicator,
  AtrIndicator,
  AccumulationDistributionIndicator,
  BollingerBands,
  MacdIndicator,
  StochasticIndicator,
  RsiIndicator,
} from "@syncfusion/ej2-react-charts";

const socket = io("http://localhost:5000");

const StocksPage = () => {
  const [ticker, setTicker] = useState("");
  const [selectedTickers, setSelectedTickers] = useState(["AAPL"]); // Track multiple stocks
  const [chartData, setChartData] = useState({});
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState({});

  const periods = [
    { intervalType: "Months", interval: 1, text: "1M" },
    { intervalType: "Months", interval: 3, text: "3M" },
    { intervalType: "Months", interval: 6, text: "6M" },
    { intervalType: "Years", interval: 1, text: "YTD" },
    { intervalType: "Years", interval: 3, text: "All" },
  ];

  // Fetch historical chart data for a given stock
  const fetchChartData = async (tickerSymbol) => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${tickerSymbol}`);
      if (!response.ok) throw new Error("Failed to fetch chart data.");

      const data = await response.json();
      if (data && data.dates && data.dates.length > 0) {
        const formattedData = data.dates.map((date, index) => ({
          x: new Date(date),
          open: data.open[index],
          high: data.high[index],
          low: data.low[index],
          close: data.close[index],
        })).filter(
          (entry) =>
            entry.x && !isNaN(entry.open) && !isNaN(entry.high) && !isNaN(entry.low) && !isNaN(entry.close)
        );

        setChartData((prevData) => ({
          ...prevData,
          [tickerSymbol]: formattedData,
        }));
        setError("");
      } else {
        setChartData((prevData) => ({
          ...prevData,
          [tickerSymbol]: [],
        }));
        setError(`No data available for ${tickerSymbol}.`);
      }
    } catch (err) {
      console.error("Error fetching chart data:", err);
      setError(err.message || "Failed to load chart data.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch data for all selected tickers
  useEffect(() => {
    selectedTickers.forEach((ticker) => fetchChartData(ticker));
  }, [selectedTickers]);

  // WebSocket for real-time updates
  useEffect(() => {
    const handleStockUpdate = (data) => {
      console.log("Real-time update:", data);
      if (data && data.price && data.timestamp) {
        setChartData((prevData) => ({
          ...prevData,
          [data.ticker]: [
            ...(prevData[data.ticker] || []),
            {
              x: new Date(data.timestamp),
              open: data.price,
              high: data.price,
              low: data.price,
              close: data.price,
            },
          ],
        }));

        setRecommendations((prev) => ({
          ...prev,
          [data.ticker]: data.recommendation || "Hold",
        }));
      }
    };

    selectedTickers.forEach((ticker) => socket.emit("track_stock", { ticker }));
    socket.on("stock_update", handleStockUpdate);

    return () => {
      selectedTickers.forEach((ticker) => socket.emit("untrack_stock", { ticker }));
      socket.off("stock_update", handleStockUpdate);
    };
  }, [selectedTickers]);

  // Add stock for tracking
  const handleSearch = () => {
    if (ticker.trim() !== "" && !selectedTickers.includes(ticker.toUpperCase())) {
      setSelectedTickers([...selectedTickers, ticker.toUpperCase()]);
      setTicker("");
    }
  };

  // Remove stock from tracking
  const handleRemoveStock = (tickerToRemove) => {
    setSelectedTickers((prev) => prev.filter((t) => t !== tickerToRemove));
  };

  return (
    <div className="stocks-page" style={{ padding: "20px" }}>
      <div className="stock-header">
        <h2 style={{ textAlign: "center" }}>üìä Stock Analysis</h2>
      </div>

      <div className="search-stock">
        <input
          type="text"
          placeholder="Enter stock ticker (e.g., AAPL)"
          value={ticker}
          onChange={(e) => setTicker(e.target.value)}
        />
        <button onClick={handleSearch}>Track Stock</button>
      </div>

      <div className="tracked-stocks">
        <h3>üîç Tracking Stocks:</h3>
        {selectedTickers.length === 0 ? (
          <p>No stocks are being tracked.</p>
        ) : (
          selectedTickers.map((ticker) => (
            <div key={ticker} className="tracked-stock">
              <span>{ticker}</span>
              <button onClick={() => handleRemoveStock(ticker)}>‚ùå Remove</button>
            </div>
          ))
        )}
      </div>

      {selectedTickers.map((ticker) => (
        <div key={ticker} className="stock-section">
          <h3>{ticker} Stock Chart</h3>
          {loading ? (
            <p>Loading...</p>
          ) : error ? (
            <p style={{ color: "red" }}>{error}</p>
          ) : chartData[ticker] && chartData[ticker].length > 0 ? (
            <ErrorBoundary>
              <StockChartComponent
                id={`stockchart-${ticker}`}
                enableSelector={true}
                primaryXAxis={{
                  valueType: "DateTime",
                  labelFormat: "MMM dd",
                  majorGridLines: { width: 0 },
                  intervalType: "Days",
                  crosshairTooltip: { enable: true },
                }}
                primaryYAxis={{
                  labelFormat: "${value}",
                  majorGridLines: { width: 0 },
                  rangePadding: "None",
                  crosshairTooltip: { enable: true },
                }}
                tooltip={{ enable: true }}
                crosshair={{ enable: true }}
                periods={periods}
                title={`${ticker} Stock Analysis`}
                height="100%"
                width="100%"
              >
                <Inject
                  services={[
                    DateTime,
                    Tooltip,
                    RangeTooltip,
                    Crosshair,
                    LineSeries,
                    CandleSeries,
                    Legend,
                    Export,
                    EmaIndicator,
                    TmaIndicator,
                    SmaIndicator,
                    MomentumIndicator,
                    AtrIndicator,
                    AccumulationDistributionIndicator,
                    BollingerBands,
                    MacdIndicator,
                    StochasticIndicator,
                    RsiIndicator,
                  ]}
                />
                <StockChartSeriesCollectionDirective>
                  <StockChartSeriesDirective
                    dataSource={chartData[ticker]}
                    xName="x"
                    open="open"
                    high="high"
                    low="low"
                    close="close"
                    type="Candle"
                    animation={{ enable: true }}
                  />
                </StockChartSeriesCollectionDirective>
              </StockChartComponent>
            </ErrorBoundary>
          ) : (
            <p>No data available.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default StocksPage;
