import React, { useEffect, useState } from "react";
import CandlestickChart from "./CandlestickChart";
import './StockScanner.css';

const StockScanner = () => {
  const [stocks, setStocks] = useState([]);
  const [minPrice, setMinPrice] = useState(""); // Minimum price input
  const [maxPrice, setMaxPrice] = useState(""); // Maximum price input
  const [error, setError] = useState("");

  // Fetch stocks based on price range
  const fetchStocks = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/test-stocks?min_price=${minPrice}&max_price=${maxPrice}`
      );
      const data = await response.json();
  
      if (response.ok) {
        setStocks(data.candidates); // Store the array of stocks
        setError("");
      } else {
        setError(data.error || "Failed to fetch stocks.");
      }
    } catch (err) {
      setError("Failed to fetch stocks.");
      console.error(err);
    }
  };
  
  

  return (
    <div className="stock-scanner">
      <h2 style={{ textAlign: "center" }}>Find Stocks by Price Range</h2>
  
      {/* Form for price range input */}
      <div className="price-filter">
        <label>
          Min Price:
          <input
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            placeholder="Enter min price"
          />
        </label>
        <label>
          Max Price:
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            placeholder="Enter max price"
          />
        </label>
        <button onClick={fetchStocks}>Search Stocks</button>
      </div>
  
      {/* Display error message */}
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
  
      {/* Display stock results */}
      <div className="stock-list">
        {stocks.length > 0 ? (
          stocks.map((stock) => (
            <CandlestickChart
              key={stock.ticker}
              ticker={stock.ticker}
              entryPoint={stock.last_price ? stock.last_price * 0.95 : 0} // Default to 0 if undefined
              exitPoint={stock.last_price ? stock.last_price * 1.10 : 0} // Default to 0 if undefined
              additionalData={`MACD: ${
                stock.macd ? stock.macd.toFixed(2) : "N/A"
              }, Signal: ${stock.signal ? stock.signal.toFixed(2) : "N/A"}`}
            />
          ))
        ) : (
          <p style={{ textAlign: "center" }}>No stocks found. Please adjust your criteria.</p>
        )}
      </div>
    </div>
  );
  
};

export default StockScanner;
