import React, { useState } from "react";
import Navbar from "./components/Navbar";
import StockScanner from "./components/StockScanner";
import TickerNewsWidget from "./components/TickerNewsWidget";

const App = () => {
  const [tickers, setTickers] = useState([]); // Store the list of displayed tickers from StockScanner

  return (
    <>
      <Navbar />
      <div className="main-container">
        {/* Stock Scanner */}
        <div className="stock-scanner-container">
          <StockScanner onTickersChange={(newTickers) => setTickers(newTickers)} />
        </div>

        {/* Ticker News Widget */}
        <div className="ticker-news-container">
          <TickerNewsWidget tickers={tickers} />
        </div>
      </div>
    </>
  );
};

export default App;
