import React, { useEffect, useState } from "react";
import CandlestickChart from "./CandlestickChart";
import './StockScanner.css';

const StockScanner = () => {
  const [stocks, setStocks] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/scan-stocks");
        const data = await response.json();
        setStocks(data.candidates);
      } catch (err) {
        setError("Failed to fetch stocks.");
        console.error(err);
      }
    };
    fetchStocks();
  }, []);

  return (
    <div className="stock-list">
      <h2 style={{ textAlign: "center" }}>Top Stocks for Gains</h2>
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
      {stocks.map((stock) => (
        <CandlestickChart
          key={stock.ticker}
          ticker={stock.ticker}
          entryPoint={stock.last_price * 0.95}
          exitPoint={stock.last_price * 1.10}
          additionalData={`Volume: ${stock.volume}`}
        />
      ))}
    </div>
  );
};

export default StockScanner;
