import React, { useEffect, useState, useRef } from "react";
import {
  ChartComponent,
  SeriesCollectionDirective,
  SeriesDirective,
  Inject,
  DateTime,
  CandleSeries,
  Tooltip,
  Zoom,
  Crosshair,
} from "@syncfusion/ej2-react-charts";
import { registerLicense } from "@syncfusion/ej2-base";

// Register the Syncfusion license
registerLicense("Ngo9BigBOggjHTQxAR8/V1NMaF5cXmRCf1FpRmJGdld5fUVHYVZUTXxaS00DNHVRdkdmWX5ednVWQ2BfVEJ+WEY=");

// Error boundary to gracefully handle runtime errors
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError() {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error("Error in chart:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return <p style={{ color: "red", textAlign: "center" }}>Something went wrong with the chart.</p>;
    }
    return this.props.children;
  }
}

const CandlestickChartStockPage = ({ ticker, entryPoint, exitPoint, additionalData }) => {
  const [chartData, setChartData] = useState([]); // Holds the candlestick data
  const [error, setError] = useState(""); // Tracks error during data fetching
  const [isMounted, setIsMounted] = useState(false); // Ensures chart is fully mounted
  const chartRef = useRef(null); // Reference for ChartComponent

  // Fetch candlestick chart data
  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();

        if (data.dates) {
          // Format data for Syncfusion
          const formattedData = data.dates.map((date, index) => ({
            x: new Date(date),
            open: data.open[index],
            high: data.high[index],
            low: data.low[index],
            close: data.close[index],
          }));
          setChartData(formattedData);
          setError("");
        } else {
          setError("No data available for this stock.");
        }
      } catch (err) {
        console.error(`Error fetching data for ${ticker}:`, err);
        setError("Failed to load chart data.");
      }
    };

    fetchChartData();
  }, [ticker]);

  // Ensure the chart is mounted before enabling features like crosshair
  useEffect(() => {
    if (chartRef.current) {
      setIsMounted(true);
    }
  }, [chartRef]);

  return (
    <ErrorBoundary>
      <div className="chart-container-stock-page">
        {error ? (
          <p style={{ color: "red", textAlign: "center" }}>{error}</p>
        ) : chartData.length === 0 ? (
          <p style={{ color: "red", textAlign: "center" }}>No chart data available.</p>
        ) : (
          <ChartComponent
            ref={chartRef}
            id={`chart-${ticker}`}
            primaryXAxis={{
              valueType: "DateTime",
              labelFormat: "MMM dd",
              intervalType: "Days",
              crosshairTooltip: { enable: isMounted && chartData.length > 0 },
            }}
            primaryYAxis={{
              labelFormat: "${value}",
              crosshairTooltip: { enable: isMounted && chartData.length > 0 },
            }}
            tooltip={{
              enable: isMounted && chartData.length > 0,
            }}
            crosshair={{
              enable: isMounted && chartData.length > 0,
              lineType: "Both",
              line: { color: "gray", width: 0.5 },
            }}
            zoomSettings={{
              enableMouseWheelZooming: true,
              enablePinchZooming: true,
              mode: "XY",
            }}
            height="100%"
            width="100%"
          >
            <Inject services={[CandleSeries, DateTime, Tooltip, Zoom, Crosshair]} />
            <SeriesCollectionDirective>
              <SeriesDirective
                dataSource={chartData}
                xName="x"
                open="open"
                high="high"
                low="low"
                close="close"
                type="Candle"
              />
            </SeriesCollectionDirective>
          </ChartComponent>
        )}
      </div>
    </ErrorBoundary>
  );
};

export default CandlestickChartStockPage;
