import React, { useState, useEffect } from "react";

const CandlestickChart = ({ ticker }) => {
  const [chartUrl, setChartUrl] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchChart = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();
        setChartUrl(`http://localhost:5000/${data.chart}`);
      } catch (err) {
        setError("Failed to load chart.");
      }
    };
    fetchChart();
  }, [ticker]);

  return (
    <div style={{ marginBottom: "20px" }}>
      <h4>{ticker} Chart</h4>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {chartUrl && (
        <img
          src={chartUrl}
          alt={`${ticker} chart`}
          style={{ width: "100%", maxWidth: "600px", height: "auto" }} // Set chart size
        />
      )}
    </div>
  );
};

export default CandlestickChart;
