import React, { useState, useEffect } from "react";
import Plot from "react-plotly.js";

const CandlestickChart = ({ ticker, entryPoint, exitPoint }) => {
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/candlestick?ticker=${ticker}`);
        const data = await response.json();

        if (data.error) {
          throw new Error(data.error);
        }

        setChartData(data); // Backend should return the candlestick data
      } catch (err) {
        setError(`Failed to fetch chart data for ${ticker}`);
        console.error(err);
      }
    };

    fetchChartData();
  }, [ticker]);

  if (error) {
    return <p style={{ color: "red" }}>{error}</p>;
  }

  if (!chartData) {
    return <p>Loading chart...</p>;
  }

  // Extract data for candlestick chart
  const { dates, open, high, low, close, currentPrice } = chartData;

  return (
    <div>
      <h4>{ticker} Chart</h4>
      <Plot
        data={[
          {
            x: dates,
            open: open,
            high: high,
            low: low,
            close: close,
            type: "candlestick",
            xaxis: "x",
            yaxis: "y",
            name: `${ticker} Candlestick`,
          },
          {
            x: [dates[0], dates[dates.length - 1]],
            y: [entryPoint, entryPoint],
            mode: "lines",
            name: "Entry Point",
            line: {
              color: "green",
              width: 2,
              dash: "dashdot",
            },
          },
          {
            x: [dates[0], dates[dates.length - 1]],
            y: [exitPoint, exitPoint],
            mode: "lines",
            name: "Exit Point",
            line: {
              color: "red",
              width: 2,
              dash: "dashdot",
            },
          },
          {
            x: [dates[0], dates[dates.length - 1]],
            y: [currentPrice, currentPrice],
            mode: "lines",
            name: "Current Price",
            line: {
              color: "blue",
              width: 2,
              dash: "solid",
            },
          },
        ]}
        layout={{
          title: `${ticker} Candlestick Chart`,
          xaxis: {
            title: "Date",
            rangeslider: { visible: true },
          },
          yaxis: {
            title: "Price",
          },
          margin: { l: 50, r: 50, t: 50, b: 50 },
        }}
        config={{ responsive: true }}
        style={{ width: "100%", height: "500px" }}
      />
    </div>
  );
};

export default CandlestickChart;
